int x = 0;
bool initialized = 0;
char inchar;
String cardName;

// ADD PROCESS/KEYBOARD APP SCRIPTS HERE
void processKB() {
  if (!initialized) {
    u8g2.clearBuffer();
    u8g2.drawStr(x, x, "Tarot Card");
    u8g2.sendBuffer();
  }
  inchar = KB().updateKeypress();

  if (inchar != 0) {
    // Return to pocketMage OS
    rebootToPocketMage();
  }

  delay(10);
}

void applicationEinkHandler() {
  static bool initialized = false;
  if (!initialized) {
    initialized = true;
    const char* majorArcana[] = {
        "The Fool",         "The Magician", "The High Priestess", "The Empress", "The Emperor",
        "The Hierophant",   "The Lovers",   "The Chariot",        "Strength",    "The Hermit",
        "Wheel of Fortune", "Justice",      "The Hanged Man",     "Death",       "Temperance",
        "The Devil",        "The Tower",    "The Star",           "The Moon",    "The Sun",
        "Judgement",        "The World"};

    static const int MAJOR_ARCANA_COUNT = sizeof(majorArcana) / sizeof(majorArcana[0]);
    // Pick a random card at app start
    static const int TAROT_COUNT = sizeof(majorArcana) / sizeof(majorArcana[0]);
    int idx = esp_random() % TAROT_COUNT;

    cardName = majorArcana[idx];
    display.setRotation(3);
    display.setFullWindow();
    display.fillScreen(GxEPD_WHITE);

    int cardWidth = 120;
    int cardHeight = 160;
    int cardX = (display.width() - cardWidth) / 2;
    int cardY = (display.height() - cardHeight) / 2;
    display.drawRect(cardX, cardY, cardWidth, cardHeight, GxEPD_BLACK);

    display.drawBitmap(cardX, cardY, tarot_image, 128, 218, GxEPD_BLACK);
    // display.drawBitmap(cardX, cardY, tarot_image, 26, 44, GxEPD_BLACK);
    Serial.printf("cardname: %s", cardName);

    // Status bar with random card name
    EINK().drawStatusBar(cardName);

    EINK().refresh();
  }
}

